mapper.update:
update sea_tags
set tag = #{tag,jdbcType=CHAR},
  usenum = #{usenum,jdbcType=INTEGER},
  vids = #{vids,jdbcType=LONGVARCHAR}
where tagid = #{tagid,jdbcType=INTEGER}

mapper.select:
select tagid, tag, usenum, vids
from sea_tags
where tagid = #{tagid,jdbcType=INTEGER}

